CREATE TABLE TB_USUARIO (ID BIGINT AUTO_INCREMENT NOT NULL, TXT_CPF VARCHAR(14) NOT NULL UNIQUE, DT_NASCIMENTO DATE, TXT_EMAIL VARCHAR(50) NOT NULL, TXT_LOGIN VARCHAR(50) NOT NULL, TXT_NOME VARCHAR(255) NOT NULL, TXT_SENHA VARCHAR(20) NOT NULL, TXT_BANDEIRA_CARTAO VARCHAR(12) NOT NULL, NUM_CARTAO VARCHAR(20) NOT NULL, PRIMARY KEY (ID))
